    /**
     * Constructor for Background
     *
     * @param gameView accessing {@link GameView} commands
     */
    public Background(GameView gameView) {
        super(gameView);
        this.width = GameView.WIDTH;
        this.height = GameView.HEIGHT - 105;
        this.position = new Position(GameView.WIDTH - GameView.WIDTH, GameView.HEIGHT / 10);
        this.size = 5;
        gameView.setColorForBlockImage('B', Color.BLUE.darker());
    }


    @Override
    public void addToCanvas() {
        gameView.addRectangleToCanvas(position.x, position.y, width, height, 1, true, Color.BLUE.darker());
        gameView.addBlockImageToCanvas(BACKGROUND_COLOR_1, position.x, position.y, size, rotation);
    }

    @Override
    public void updateStatus() {

    }
    /**
     * Clones the Background.
     * @return clone.
     */
    @Override
    public Background clone(){
        return (Background) super.clone();
    }
    /**
     *Compares String of Background.
     * @param o object.
     * @return true or false
     */
    @Override
    public boolean equals(Object o) {
        if (this == o) return true;
        if (o == null || getClass() != o.getClass()) return false;
        if (!super.equals(o)) return false;
        Background background = (Background) o;
        return size == size;
    }
    /**
     * Creates hashcode for the color-changing-block.
     * @return hashcode.
     */
    @Override
    public int hashCode() {
        return Objects.hash(super.hashCode(), size);
    }
